binding.script:
			var mode = getprop("/controls/shuttle/ku-function");
			
			if (mode == 0) {SpaceShuttle.antenna_manager.rr_mode = "GPC";}
			else if (mode == 1) {SpaceShuttle.antenna_manager.rr_mode = "GPC DESIG";}
			else if (mode == 2) {SpaceShuttle.antenna_manager.rr_mode = "AUTO TRACK";}
			else if (mode == 3) {SpaceShuttle.antenna_manager.rr_mode = "MAN SLEW";}

			setprop("/sim/gui/dialogs/SpaceShuttle/ku-antenna/control", SpaceShuttle.antenna_manager.rr_mode);

var mode = getprop("/controls/shuttle/ku-mode");

if (mode == -1) {SpaceShuttle.antenna_manager.function = "COMM";}
else if (mode == 0) {SpaceShuttle.antenna_manager.function = "RDR PASSIVE";}
else if (mode == 1) {SpaceShuttle.antenna_manager.function = "RDR COOP";}
setprop("/sim/gui/dialogs/SpaceShuttle/ku-antenna/function", SpaceShuttle.antenna_manager.function);

var mode = getprop("/fdm/jsbsim/systems/antenna/s-pm-mode-selection-switch");
if (mode == 0)
	{
	setprop("/sim/gui/dialogs/SpaceShuttle/S-antenna/mode", "SGLS");
	SpaceShuttle.antenna_manager.mode = "SGLS";
	}
else if (mode == 1)
	{
	setprop("/sim/gui/dialogs/SpaceShuttle/S-antenna/mode", "S-LO");
	SpaceShuttle.antenna_manager.mode = "S-LO";
	}
else if (mode == 2)
	{
	setprop("/sim/gui/dialogs/SpaceShuttle/S-antenna/mode", "S-HI");
	SpaceShuttle.antenna_manager.mode = "S-HI";
	}
else if ((mode == 3) or (mode == 4))
	{
	setprop("/sim/gui/dialogs/SpaceShuttle/S-antenna/mode", "TDRS");
	SpaceShuttle.antenna_manager.mode = "TDRS";
	}

                    var sel = getprop("/fdm/jsbsim/systems/fcs/sense-switch");
                    if (sel == -1) {
                        setprop("/fdm/jsbsim/systems/fcs/sense-X", 0);
	                    setprop("/fdm/jsbsim/systems/fcs/sense-minus-X", 0);
	                    setprop("/fdm/jsbsim/systems/fcs/sense-minus-Z", 1);
                    } else
                    if (sel == 0) {
                        setprop("/fdm/jsbsim/systems/fcs/sense-X", 1);
	                    setprop("/fdm/jsbsim/systems/fcs/sense-minus-X", 0);
	                    setprop("/fdm/jsbsim/systems/fcs/sense-minus-Z", 0);
                    } else
                    if (sel == 1) {
                        setprop("/fdm/jsbsim/systems/fcs/sense-X", 0);
	                    setprop("/fdm/jsbsim/systems/fcs/sense-minus-X", 1);
	                    setprop("/fdm/jsbsim/systems/fcs/sense-minus-Z", 0);
                    }

var state = getprop("/fdm/jsbsim/systems/rcs-hardware/heater-fwd-status");
if (state == 1)
	{
	setprop("/fdm/jsbsim/systems/rcs-hardware/heater-fwd-A-status", 1);
	setprop("/fdm/jsbsim/systems/rcs-hardware/heater-fwd-B-status", 0);
	}
else if (state == -1)
	{
	setprop("/fdm/jsbsim/systems/rcs-hardware/heater-fwd-A-status", 0);
	setprop("/fdm/jsbsim/systems/rcs-hardware/heater-fwd-B-status", 1);
	}
else
	{
	setprop("/fdm/jsbsim/systems/rcs-hardware/heater-fwd-A-status", 0);
	setprop("/fdm/jsbsim/systems/rcs-hardware/heater-fwd-B-status", 0);
	}

                    var channel1_set = getprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos1");
                    channel1_set = channel1_set + 1;
                    if (channel1_set > 9) channel1_set = 9;
                    setprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos1", channel1_set);
		    SpaceShuttle.mls_system.receiver[0].set_channel(channel1_set);

                    var channel1_set = getprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos1");
                    channel1_set = channel1_set - 1;
                    if (0 > channel1_set) channel1_set = 0;
                    setprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos1", channel1_set);
		    SpaceShuttle.mls_system.receiver[0].set_channel(channel1_set);

                    var channel2_set = getprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos2");
                    channel2_set = channel2_set + 1;
                    if (channel2_set > 9) channel2_set = 9;
                    setprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos2", channel2_set);
		    SpaceShuttle.mls_system.receiver[1].set_channel(channel2_set);

                    var channel2_set = getprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos2");
                    channel2_set = channel2_set - 1;
                    if (0 > channel2_set) channel2_set = 0;
                    setprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos2", channel2_set);
		    SpaceShuttle.mls_system.receiver[1].set_channel(channel2_set);

                    var channel3_set = getprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos3");
                    channel3_set = channel3_set + 1;
                    if (channel3_set > 9) channel3_set = 9;
                    setprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos3", channel3_set);
		    SpaceShuttle.mls_system.receiver[2].set_channel(channel3_set);

                    var channel3_set = getprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos3");
                    channel3_set = channel3_set - 1;
                    if (0 > channel3_set) channel3_set = 0;
                    setprop("/fdm/jsbsim/systems/navigation/mls-channel-set-pos3", channel3_set);
		    SpaceShuttle.mls_system.receiver[2].set_channel(channel3_set);
