binding.script:
var selection = getprop("/fdm/jsbsim/systems/dps/crtl-sel");
if (selection == 1)
{
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-1-selected", 1);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-2-selected", 0);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-3-selected", 0);
    SpaceShuttle.kb_array[0].assign_idp(1);
}
else if (selection == 0)
{
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-1-selected", 0);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-2-selected", 0);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-3-selected", 1);
    SpaceShuttle.kb_array[0].assign_idp(3);
}

var selection = getprop("/fdm/jsbsim/systems/dps/crtr-sel");
if (selection == 1)
{
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-1-selected", 0);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-2-selected", 0);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-3-selected", 1);
    SpaceShuttle.kb_array[1].assign_idp(3);
}
else if (selection == 0)
{
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-1-selected", 0);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-2-selected", 1);
    setprop("/fdm/jsbsim/systems/dps/virtual-kb-idp-3-selected", 0);
    SpaceShuttle.kb_array[1].assign_idp(2);
}

#switch-pos = cmdarg().getNode('offset').getValue();
if (getprop("/fdm/jsbsim/systems/bodyflap") == 0) 
    SpaceShuttle.bodyflap_up();
else if (getprop("/fdm/jsbsim/systems/bodyflap") == 2)
    SpaceShuttle.bodyflap_down();
