    {
        setprop("/fdm/jsbsim/systems/abort/arm-rtls", 1);
        setprop("/fdm/jsbsim/systems/abort/arm-ato", 0);
        setprop("/fdm/jsbsim/systems/abort/arm-tal", 0);
    }
 else
    {
        setprop("/fdm/jsbsim/systems/abort/arm-rtls", 0);
        setprop("/fdm/jsbsim/systems/abort/arm-ato", 0);
        setprop("/fdm/jsbsim/systems/abort/arm-tal", 0);
    }

                    if (getprop("/fdm/jsbsim/systems/abort/abort-cmd-ss") == 0)
                        {
                            setprop("/fdm/jsbsim/systems/abort/abort-cmd-ss", 1);

                            var status_tal = getprop("/fdm/jsbsim/systems/abort/arm-tal");
                            var status_ato = getprop("/fdm/jsbsim/systems/abort/arm-ato");
                            var status_rtls = getprop("/fdm/jsbsim/systems/abort/arm-rtls");

                            if (status_tal == 1)
                              {
                                setprop("/fdm/jsbsim/systems/abort/abort-mode", 2);
                                var tal_site_index = getprop("/fdm/jsbsim/systems/entry_guidance/tal-site-iloaded");
                                SpaceShuttle.update_site_by_index(tal_site_index);
                                setprop("/fdm/jsbsim/systems/entry_guidance/guidance-mode", 2);
				SpaceShuttle.entry_guidance_available = 1;
                              }
                            else if (status_ato == 1)
                              {
			                    SpaceShuttle.init_ato();
                              }
                            else if (status_rtls == 1)
                              {
                                SpaceShuttle.init_rtls();
                              }
                            SpaceShuttle.click("avionics");
                        }

var mode_cmd = getprop("/fdm/jsbsim/systems/fcs/entry-mode-switch");
var mach = getprop("/fdm/jsbsim/velocities/mach");

if (mach > 3.5)
	{
	if (mode_cmd == 2)
		{
		setprop("/fdm/jsbsim/systems/fcs/rcs-yaw-mode", 0);
		setprop("/fdm/jsbsim/systems/fcs/no-y-jet", 1);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-no-y-jet", 1);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-lo-gain", 0);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-auto", 0);
		}
	else if (mode_cmd == 1)
		{
		setprop("/fdm/jsbsim/systems/fcs/rcs-yaw-mode", 1);
		setprop("/fdm/jsbsim/systems/fcs/no-y-jet", 0);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-no-y-jet", 0);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-lo-gain", 1);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-auto", 0);
		}
	else
		{
		setprop("/fdm/jsbsim/systems/fcs/rcs-yaw-mode", 1);
		setprop("/fdm/jsbsim/systems/fcs/no-y-jet", 0);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-no-y-jet", 0);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-lo-gain", 0);
		setprop("/fdm/jsbsim/systems/fcs/entry-mode-auto", 1);
		}
	}
